package com.example.a98application;import android.annotation.SuppressLint;import android.support.v7.app.AppCompatActivity;import android.os.Bundle;import android.text.TextUtils;import android.view.View;import android.view.View.OnClickListener;import android.widget.Button;import android.widget.EditText;import android.widget.TextView;public class Areaofsquare extends AppCompatActivity implements View.OnClickListener{    private EditText length;    private TextView show;    public Button calculate;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_areaofsquare);        length = findViewById(R.id.editTextNumber);        show = findViewById(R.id.textView3);        calculate = findViewById(R.id.button6);        calculate.setOnClickListener(this);    }    @Override    public void onClick(View v) {        if(v.getId() == R.id.button6){            String length_string = length.getText().toString().trim();            boolean abc = false;            if(TextUtils.isEmpty( length_string)) {                abc = true;                length.setError(" incorrect data");            }            if (! abc){                double length = Double.parseDouble(length_string);                double ans = length*length;                show.setText( String.valueOf(ans));            }        }    }}